{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "Large",
                                    "weight": "Bolder",
                                    "text": "**Azure Active Directory**"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "Image",
                                    "url": "https://raw.githubusercontent.com/videor/AutoPilotConditionalAccess/master/AutoPilotConditionalAccess/azure-quickstart-templates/301-conditionalaccess-alerts-automation/images/Alert.PNG",
                                    "altText": "Pending",
                                    "height": "30px"
                                }
                            ],
                            "width": "auto"
                        }
                    ]
                }
            ],
            "bleed": true
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "ExtraLarge",
                                    "text": "Conditional Access",
                                    "wrap": true
                                },
                                {
                                    "type": "TextBlock",
                                    "spacing": "Small",
                                    "size": "Small",
                                    "weight": "Bolder",
                                    "color": "Accent",
                                    "text": "Alert"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "ActionSet",
                                    "actions": [
                                        {
                                        }
                                    ]
                                }
                            ],
                            "width": "auto"
                        }
                    ]
                },
                {
                    "type": "FactSet",
                    "spacing": "Large",
                    "facts": [
                        {
                            "title": "Type",
                            "value": "**Alert: Conditional Access policy change alert**"
                        },     
                        {
                            "title": "Policy Name",
                            "value": "@{items('For_each_2')?['displayName']}"
                        },
                        {
                            "title": "Policy ID",
                            "value": "@{items('For_each_2')?['id']}"
                        },
                       {
                            "title": "Activity Type",
                            "value": "@{body('Parse_Audit_Logs')?['activityDisplayName']}"
                        },
                        {
                            "title": "Activity Time",
                            "value": "@{body('Parse_Audit_Logs')?['activityDateTime']}"
                        },
                        {
                            "title": "Policy changed by",
                            "value": "@{body('Parse_Audit_Logs')?['initiatedBy']?['user']?['userPrincipalName']}"
                        },
                        {
                            "title": "Message",
                            "value": "A new conditional access policy change has occured in your PROD environment. This alert requires acknowledgement from one of the members in this channel.  Message also reported to secops@contoso.com"
                        }
                    ]
                }
            ]
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "ActionSet",
                    "actions": [
                        {
                            "type": "Action.Submit",
                            "title": "Acknowledge as planned change to Conditional Access",
                            "style": "positive",
                            "data": {
                                "id": "_qkQW8dJlUeLVi7ZMEzYVw",
                                "action": "approve"
                            }
                        },
                        {
                            "type": "Action.ShowCard",
                            "title": "Notify compliance team for investigation",
                            "style": "destructive",
                            "card": {
                                "type": "AdaptiveCard",
                                "body": [
                                    {
                                        "type": "Input.Text",
                                        "id": "RejectCommentID",
                                        "placeholder": "Please specify an appropriate reason for why you think this is a suspicious policy change.",
                                        "isMultiline": true
                                    }
                                ],
                                "actions": [
                                    {
                                        "type": "Action.Submit",
                                        "title": "Send",
                                        "data": {
                                            "id": "_qkQW8dJlUeLVi7ZMEzYVw",
                                            "action": "reject"
                                        }
                                    }
                                ],
                                "$schema": "http://adaptivecards.io/schemas/adaptive-card.json"
                            }
                        }
                    ]
                }
            ]
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2",
    "fallbackText": "This card requires Adaptive Cards v1.2 support to be rendered properly."
}

